interventions.Journal Template:
{
  "Trigger": {
    "question": "Describe the situation that led to your negative emotions or thoughts.",
    "example": "My manager gave me critical feedback during a team meeting."
  },
  "Feelings": {
    "question": "How did you feel in the moment?",
    "example": "Embarrassed, anxious, and upset."
  },
  "Automatic Thoughts": {
    "question": "Write down the immediate thoughts that came to mind when the situation occurred.",
    "example": "I'm terrible at my job. Everyone thinks I'm incompetent."
  },
  "Evidence For/Against": {
    "question": [
      "What evidence do you have to support your negative thoughts?",
      "What evidence do you have to challenge or contradict your negative thoughts?"
    ],
    "example": [
      "I have received positive feedback from my manager in the past.",
      "There have been times when my family has listened to me and understood my concerns."
    ]
  },
  "Alternative Thinking": {
    "question": "Write down a healthier way of thinking about the situation.",
    "example": [
      "I can learn from this feedback and improve my performance.",
      "My family may not always listen, but they care about me and want the best for me."
    ]
  },
  "Affirmations": {
    "question": "Create a list of affirmations or positive statements to reinforce your new, balanced thoughts.",
    "example": [
      "I am capable of learning and improving.",
      "Mistakes are opportunities for growth."
    ]
  },
  "Action Plan": {
    "question": "What action can you take to support your new thinking?",
    "example": [
      "Seek additional training or resources to improve my skills.",
      "Communicate openly with my family about their listening habits and work on improving our communication."
    ]
  }
}

