interventions.Journal Template:
{
  "Identify the Trigger": {
    "instructions": "Describe the situation that led to your negative emotions or thoughts.",
    "questions": [
      "What happened?",
      "Where were you?",
      "Who was involved?",
      "How did you feel in the moment?"
    ]
  },
  "Record Your Automatic Thoughts": {
    "instructions": "Write down the immediate thoughts that came to mind when the situation occurred. These are often negative, irrational, or distorted.",
    "questions": [
      "What were you thinking?",
      "What meaning did you attach to the situation?"
    ]
  },
  "Challenge Your Thoughts": {
    "instructions": "Examine the validity of your automatic thoughts.",
    "questions": [
      "Is there another way to look at this situation?",
      "Are there any facts that contradict my negative thoughts?"
    ]
  },
  "Replace with Healthier Beliefs": {
    "instructions": "Write down a healthier way of thinking.",
    "questions": [
      "What is a more balanced and rational alternative to my negative thoughts?",
      "How can I reframe this situation positively?"
    ]
  },
  "Affirmations": {
    "instructions": "Create a list of affirmations or positive statements to reinforce your new, balanced thoughts.",
    "questions": [
      "What positive statements can I use to remind myself of my healthier beliefs?"
    ]
  },
  "Action Plan": {
    "instructions": "Identify actions you can take to support your new thinking.",
    "questions": [
      "What steps can I take to avoid falling into the same negative thought patterns in the future?",
      "How can I incorporate healthier behaviors into my daily routine?"
    ]
  }
}

