interventions.Journal Template:
{
  "Identify the Trigger": {
    "Instructions": "Describe the situation that led to your negative emotions or thoughts.",
    "Questions": [
      "What happened?",
      "Where were you?",
      "Who was involved?",
      "How did you feel in the moment?"
    ]
  },
  "Record Your Automatic Thoughts": {
    "Instructions": "Write down the immediate thoughts that came to mind when the situation occurred.",
    "Questions": [
      "What were you thinking?",
      "What meaning did you attach to the situation?"
    ]
  },
  "Challenge Your Thoughts": {
    "Instructions": "Ask yourself if your thoughts are based on facts or assumptions.",
    "Questions": [
      "Is this thought based on facts or assumptions?",
      "Are there other ways to look at this situation?"
    ]
  },
  "Reframe Your Thoughts": {
    "Instructions": "Replace negative thoughts with more balanced and rational alternatives.",
    "Questions": [
      "What is a more balanced way to think about this situation?",
      "How can I reframe this thought to make it more realistic?"
    ]
  },
  "Affirmations": {
    "Instructions": "Create a list of affirmations or positive statements to reinforce your new thoughts.",
    "Questions": [
      "What positive statement can I use to counteract this negative thought?",
      "How can I remind myself of this affirmation in challenging situations?"
    ]
  },
  "Action Plan": {
    "Instructions": "Identify actions you can take to support your new thinking.",
    "Questions": [
      "What action can I take to support my new thinking?",
      "How can I implement this action plan in my daily life?"
    ]
  }
}

