{
  "Identify the Trigger": {
    "instructions": "Describe the situation that led to your negative emotions or thoughts.",
    "questions": [
      "What happened?",
      "Where were you?",
      "Who was involved?",
      "How did you feel in the moment?"
    ]
  },
  "Record Your Automatic Thoughts": {
    "instructions": "Write down the immediate thoughts that came to mind when the situation occurred. These are often negative, irrational, or distorted.",
    "questions": [
      "What were you thinking?",
      "What meaning did you attach to the situation?"
    ]
  },
  "Challenge Your Thoughts": {
    "instructions": "Challenge your automatic thoughts by considering alternative perspectives.",
    "questions": [
      "Is there another way to look at this situation?",
      "Are there any facts that contradict my negative thoughts?"
    ]
  },
  "Replace with Balanced Thoughts": {
    "instructions": "Replace your negative thoughts with more balanced and realistic ones.",
    "questions": [
      "What would a more balanced thought be in this situation?",
      "How can I reframe this thought to be more positive?"
    ]
  },
  "Affirmations": {
    "instructions": "Create a list of affirmations or positive statements to reinforce your new, balanced thoughts.",
    "questions": [
      "What positive statements can I use to reinforce my new thoughts?",
      "How can I repeat these affirmations to myself?"
    ]
  },
  "Action Plan": {
    "instructions": "Identify actions you can take to support your new thinking.",
    "questions": [
      "What action can I take to support my new thinking?",
      "How can I apply this new perspective in future situations?"
    ]
  },
  "Progress Tracking": {
    "instructions": "Track your progress over time to see how your thinking and emotions have changed.",
    "questions": [
      "Do I feel slightly better or more optimistic after challenging my thoughts?",
      "Have I noticed any patterns in my negative thoughts?"
    ]
  }
}

