{
  "Trigger": {
    "instructions": "Describe the situation that led to your negative emotions or thoughts.",
    "questions": [
      "What happened?",
      "Where were you?",
      "Who was involved?",
      "How did you feel in the moment?"
    ]
  },
  "Automatic Thoughts": {
    "instructions": "Write down the immediate thoughts that came to mind when the situation occurred. These are often negative, irrational, or distorted.",
    "questions": [
      "What were you thinking?",
      "What meaning did you attach to the situation?"
    ]
  },
  "Cognitive Distortions": {
    "instructions": "Identify any cognitive distortions (e.g., all-or-nothing thinking, catastrophizing) in your automatic thoughts.",
    "questions": [
      "Is my thinking all-or-nothing?",
      "Am I catastrophizing the situation?",
      "Are there other distortions present?"
    ]
  },
  "Challenging Evidence": {
    "instructions": "Challenge your automatic thoughts with evidence.",
    "questions": [
      "Is there another way to look at this situation?",
      "What evidence do I have to support my thoughts?",
      "Are there any facts that contradict my thoughts?"
    ]
  },
  "Alternative Thinking": {
    "instructions": "Write down a healthier way of thinking.",
    "questions": [
      "What would a more balanced perspective be?",
      "How can I reframe this situation positively?"
    ]
  },
  "Affirmations": {
    "instructions": "Create a list of affirmations or positive statements to reinforce your new, balanced thoughts.",
    "questions": [
      "What positive statements can I use to reinforce my new thoughts?",
      "How can I remind myself of these affirmations?"
    ]
  },
  "Action Plan": {
    "instructions": "What action can you take to support your new thinking?",
    "questions": [
      "What steps can I take to address this situation differently?",
      "How can I apply my new thinking in real-life situations?"
    ]
  },
  "Improvement": {
    "instructions": "Do you feel slightly better or more optimistic after challenging your thoughts?",
    "questions": [
      "How do I feel now compared to before?",
      "Have I noticed any improvements in my mood or behavior?"
    ]
  }
}

